import React from 'react'
import{useRef,useState} from 'react'
import'../components/banner.css'
import phone from '../images/phone.jpg'
import adress from '../images/adress.png'
import email from '../images/email.jpg'
import emailjs from 'emailjs-com';
const Contact = () => {
    const [name, setName] = useState('');
    const handleName = e => {
      setName(e.currentTarget.value);
      };

    const [subject, setSubject] = useState('');
    const handleSubject = e => {
        setSubject(e.currentTarget.value); 
    }

    const [email, setEmail] = useState('');
    const handleEmail = e => {
        setEmail(e.currentTarget.value);
    };

    const [message, setMessage] = useState('');
    const handleMessage = e => {
        setMessage(e.currentTarget.value);
  };

    const [Done, setDone] = useState('')
    const formref=useRef()
    const hundleSbmit=(e)=>{
e.preventDefault()
emailjs.sendForm('service_ciovmx1', 'template_v9wnil9',formref, 'user_DJNvGM2CEM6WTHGTa1XO6YOUR_USER_ID')
.then((result) => {
    console.log(result.text);
    setDone(true)
}, (error) => {
    console.log(error.text);
});
};
    
    return (
        <div>
<div className='c'>
<div className='c-bg'></div>
<div className='c-wrapper'>
<div className="c-left">
    <h1 className='c-title'> let's discuss your project</h1>
    <div className='c-info'>
        <div className='c-info-item'>
        <img src= {phone} alt="phone-img"   className='c-icon' />
        +1 123 455 675
        </div>
        <div className='c-info-item'>
        <img src= {email} alt="phone-img" className='c-icon' />
        contact@lama.dev
        </div>
        <div className='c-info-item'>
        <img src= {adress} alt="phone-img" className='c-icon' />
    250 king street,
        </div>
</div>
</div>
<div className='c-right'>
    
<p className='c-decs'>  <b>what's your story ?</b> it is a long established fact that a reader will be distracted by the relabel content .<br />
i design and develop services for customers of all sizes , specializing in creating stylish , modern websites ,subseries on story
</p>
<form ref={formref} onSubmit={hundleSbmit}>
    <input type='text' placeholder='Name'  value={name}  name='user_name'/><br/>
    <input type='text' placeholder='subject' value={subject} name='user_subject'/><br/>
    <input type='text' placeholder='email' value={email} name='user_email'/><br/>
    <textarea rows='5' cols='10' placeholder='message' value={meassage} name='meassage'/><br/>
    <button>submit</button>
    {Done&& 'thank you'}
</form>
</div>
</div>
</div> 
</div>
    )}

export default Contact;
